    void changeStyle(){
      if(popBold.isSelected() && popItatic.isSelected()){
        txtComment.setFont(new Font("Arial",Font.BOLD|Font.ITALIC,24));
      }else if(popBold.isSelected() && !popItatic.isSelected()){
        txtComment.setFont(new Font("Arial",Font.BOLD,24));
      }else if(!popBold.isSelected() && popItatic.isSelected()){
        txtComment.setFont(new Font("Arial",Font.ITALIC,24));
      }else{
        txtComment.setFont(new Font("Arial",Font.PLAIN,24));
      }
    }
    private void popBoldItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_popBoldItemStateChanged
      changeStyle();
    }//GEN-LAST:event_popBoldItemStateChanged

